<?php
function getChildren($post_id) {
	$children_args = array(
		'post_parent' => $post_id,
		'post_type' => 'page',
		'numberposts' => -1,
		'post_status' => 'publish',
		'orderby' => 'menu_order',
		'order' => 'ASC'
	);
	$children = get_children($children_args);
	$output = null;
	if(!empty($children)) {
		echo '<ul>';
		foreach($children as $child) {
			echo '<li><a class="button cresstec--dark--blue" href="'.get_permalink($child->ID).'">' . $child->post_title .'</a></li>';
			getChildren($child->ID);
		}
		echo '</ul>';
	}
}
